var xhr = new XMLHttpRequest();
xhr.open('GET','https://pixabay.com/api/?key=2717787-691d61cdcb6847f3180471c5d&q=yellow+flowers&image_type=photo&pretty=true',true);
xhr.send();

xhr.onreadystatechange = function() { // (3)
  if (xhr.readyState != 4) return;


  if (xhr.status != 200) {
    alert(xhr.status + ': ' + xhr.statusText);
  } else {
    alert(xhr.responseText);
  }

};

